INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('This Pokmon has 1.5 its Special Attack when burned.}', 138, 9);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Pokmon mit dieser Fhigkeit haben nach jeder Runde eine Chance von 50% eine verbrauchte Beere wiederherzustellen, wenn das letzte verbrauchte Item eine Beere war und es gerade kein Item trgt. Bei starkem Sonnenlicht ist die Chance 100%.}', 139, 6);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('After each turn, if the last item this Pokmon consumed was a Berry and it is not currently holding an item, it has a 50% chance of regaining that Berry, or a 100% chance during strong sunlight.}', 139, 9);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Pokmon mit dieser Fhigkeit erleiden keinen Schaden von Attacken von Teampartnern, inklusive solchen Attacken die nur ein Ziel treffen und direkt auf das Pokmon gerichtet sind.}', 140, 6);
