INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Die normal Attacken von Pokémon mit dieser Fäigkeit werden zu fairy Attacken. Attacken die durch diese Fähigkeit beeinflusst werden, haben 1,3× so viel Basisstärke wie sonst.}', 182, 6);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Turns the bearer''s normal-type moves into fairy moves.  Moves changed by this ability have 1.3× their power.}', 182, 9);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Wenn ein Pokémon mit dieser Fähigkeit von einer Attacke die Kontakt herstellt getroffen wird, wird die speed des Angreifers um 1 Stufe gesenkt.}', 183, 6);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Lowers attacking Pokémon''s Speed by one stage on contact.}', 183, 9);
