INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Turns the bearer''s normal-type moves into flying-type moves.  Moves changed by this ability have 1.3× their power.}', 184, 9);
INSERT INTO ABILITY_EFFECTS (effect, ability_id, language_id) VALUES ('Wenn ein Pokémon mit dieser Fähigkeit eine Attacke einsetzt die Schaden verursacht, trifft die Attacke ein zweites Mal, wobei der zweite Schlag nur mit halber Stärke ausgeführt wird.}', 185, 6);
